var canvasElem = document.getElementById('canvas_draw');
var context = canvasElem.getContext("2d");
var clickX = new Array();
var clickY = new Array();
var clickDrag = new Array();
var paint;

function addClick(x, y, dragging)
{
  clickX.push(x);
  clickY.push(y);
  clickDrag.push(dragging);
}

function clear_canvas(){
  context.fillStyle = '#FFFFFF'
  context.fillRect(0, 0, context.canvas.width, context.canvas.height); // Clears the canvas

  clickX = new Array();
  clickY = new Array();
  clickDrag = new Array();
}

function redraw(){
  context.fillStyle = '#FFFFFF'
  context.fillRect(0, 0, context.canvas.width, context.canvas.height); // Clears the canvas

  context.strokeStyle = "#000000";
  context.lineJoin = "round";
  context.lineWidth = 3;
			
  for(var i=0; i < clickX.length; i++) {		
    context.beginPath();
    if(clickDrag[i] && i){
      context.moveTo(clickX[i-1], clickY[i-1]);
     }else{
       context.moveTo(clickX[i]-1, clickY[i]);
     }
     context.lineTo(clickX[i], clickY[i]);
     context.closePath();
     context.stroke();
  }
}

$('#canvas_draw').mousedown(function(e){
  var rect = canvasElem.getBoundingClientRect();
  var mouseX = e.clientX - rect.left;
  var mouseY = e.clientY - rect.top;

  paint = true;
  addClick(mouseX, mouseY);
  redraw();
});

$('#canvas_draw').mousemove(function(e){
  if(paint){
    var rect = canvasElem.getBoundingClientRect();
    var mouseX = e.clientX - rect.left;
    var mouseY = e.clientY - rect.top;

    addClick(mouseX, mouseY, true);
    redraw();
  }
});

$('#canvas_draw').mouseup(function(e){
  paint = false;
});

$('#canvas_draw').mouseleave(function(e){
  paint = false;
});
